mapper.select:
SELECT q.question_id, q.question_name, q.question_score, qt.question_type_name, qc.question_category_name FROM question q LEFT JOIN question_type qt ON qt.question_type_id = q.question_type_id LEFT JOIN question_category qc ON qc.question_category_id = q.question_category_id
and q.question_category_id = #{categoryId}
and q.question_type_id = #{typeId}
ORDER BY q.question_id

SELECT q.question_id, q.question_name, qa.answer, q.question_score, qt.question_type_name, qc.question_category_name FROM question q LEFT JOIN question_type qt ON qt.question_type_id = q.question_type_id LEFT JOIN question_category qc ON qc.question_category_id = q.question_category_id LEFT JOIN question_answer qa ON qa.question_id = q.question_id
and q.question_category_id = #{categoryId}
and q.question_type_id = #{typeId}
ORDER BY q.question_id
